mapper.select:
SELECT count(0)
FROM t_finance_intro


        and id = #{id}


        and index_type = #{indexType}


        and code_number like concat("%",#{codeNumber},"%")


        and shares_name like concat("%",#{sharesName},"%")

SELECT id, index_type as indexType, code_number as codeNumber, shares_name as sharesName,shares_alise as sharesAlise, shares_total_number as sharesTotalNumber, shares_allow_total_number as sharesAllowTotalNumber, status, date_format(load_time, '%Y.%m.%d %T') loadTime, date_format(create_time, '%Y.%m.%d %T') createTime, date_format(update_time, '%Y.%m.%d %T') updateTime,remarks FROM t_finance_intro
and id = #{id}
and index_type = #{indexType}
and code_number like concat("%",#{codeNumber},"%")
and shares_name like concat("%",#{sharesName},"%")
ORDER BY create_time DESC LIMIT #{offSet}, #{pageRow}
