mapper.select:
SELECT count(0)
FROM t_finance_update


        and id = #{id}


        and shares_id = #{sharesId}


        and status = #{status}


        and remarks like concat("%",#{remarks},"%")

SELECT
id,
shares_id as sharesId, status,
date_format(create_time, '%Y.%m.%d %T') createTime,
remarks,fail_num as failNum
FROM t_finance_update


        and id = #{id}


        and shares_id = #{sharesId}


        and status = #{status}


        and remarks like concat("%",#{remarks},"%")


ORDER BY create_time DESC
LIMIT #{offSet}, #{pageRow}
